<?php /** * 调用kindeditor编辑器 * $this->widget('application.ext.kindeditor',array('id'=>'Pages_content')); * @author 轻飘如羽 <yiiphp@gmail.com> * @link http://www.fowcms.com/ */	class kindeditor extends CWidget{	public $id;  	function run(){		$path = Yii::app()->request->baseUrl."/lib/kindeditor";		F::script($path.'/kindeditor-min.js');		F::rgScript($this->id.'kedtior',"			KE.show({				id : '{$this->id}'			}); 		"		);		 	 	}}