<?php/* * To change this template, choose Tools | Templates * and open the template in the editor. */class WCategory extends CWidget {    public function init() {        parent::init();    }    public function run() {        $key = $_REQUEST['key'];        $model = Category::model()->findByAttributes(array('url' => $key));        $ids = $model->getMeChildsId($model->category_id);        $this->render('category', array(            'model' => $model,        ));    }}