<?phpclass DefaultController extends Controller {    public function filters() {        return array('rights',);    }    public function allowedActions() {//            return 'index';    }    public function actionIndex() {        Yii::app()->theme = 'admin';        $this->render('index');    }    /**     * This is the action to handle external exceptions.     */    public function actionError() {        if ($error = Yii::app()->errorHandler->error) {            if (Yii::app()->request->isAjaxRequest)                echo $error['message'];            else                $this->render('error', $error);        }    }}