<div class="form">    <?php    $form = $this->beginWidget('CActiveForm', array(                'id' => 'category-form',                'enableAjaxValidation' => false,            ));    ?>    <p class="note">Fields with <span class="required">*</span> are required.</p>    <?php echo $form->errorSummary($model); ?>    <div class="row">        <?php echo $form->labelEx($model, 'cate_name'); ?>        <?php echo $form->textField($model, 'cate_name', array('size' => 60, 'maxlength' => 100)); ?>        <?php echo $form->error($model, 'cate_name'); ?>    </div>    <div class="row">        <?php echo $form->labelEx($model, 'parent_id'); ?>        <select name="Category[parent_id]" id="Category_parent_id">                 <?php echo $this->parent;?>         </select>         <?php echo $form->error($model, 'parent_id'); ?>    </div>    <div class="row">		<?php echo $form->labelEx($model,'url'); ?>		<?php echo $form->textField($model,'url',array('size'=>60,'maxlength'=>200)); ?>		<?php echo $form->error($model,'url'); ?>    </div>    <div class="row">        <?php echo $form->labelEx($model, 'sort_order'); ?>        <?php echo $form->textField($model, 'sort_order'); ?>        <?php echo $form->error($model, 'sort_order'); ?>    </div>    <div class="row">        <?php echo $form->labelEx($model, 'if_show'); ?>        <?php echo $form->dropDownList($model, 'if_show', array('1' => '是', '0' => '否')); ?>        <?php echo $form->error($model, 'if_show'); ?>    </div>    <div class="row buttons">        <?php echo CHtml::submitButton($model->isNewRecord ? 'Create' : 'Save'); ?>    </div>    <?php $this->endWidget(); ?></div><!-- form --><script type="text/javascript">	$(function(){ 		var tid = "<?php echo $model->parent_id;?>";					$("#Category_parent_id option").each(function(i){						if(this.value == tid)						{							$(this).attr("selected","selected");						} 					}); 	});</script>