<?phpYii::app()->clientScript->registerCoreScript('jquery');Yii::app()->clientScript->registerScriptFile(Yii::app()->request->baseUrl . '/css/jquery.highlight-3.js');?><script type="text/javascript"><?php if ($keyword) { ?>            $(document).ready(function(){                $(".product_name").removeHighlight().highlight("<?php echo $keyword ?>");            })<?php } ?></script><style type="text/css">    .highlight { background-color: yellow }</style><?php$this->pageTitle = Yii::app()->name . ' - 产品中心';$this->breadcrumbs = array(    '产品中心',);?><div class="box">    <div class="box-title">产品列表</div>    <div class="product-inside">        <ul><?phpif ($product) {    foreach ($product as $p) {        ?>                    <li>                        <table width="180"  height="230" border="0" cellspacing="0" cellpadding="0">                            <tr>                                <td align="center" height="180" width="180"><?php echo $p->getIndexThumb() ?></td>                            </tr>                            <tr>                                <td align="center" class="product_name">名称：<?php echo $p->product_name ?></td>                            </tr>                            <tr>                                <td align="center" class="product_name">分类：<?php echo $p->cat->cate_name ?></td>                            </tr>                        </table>                    </li>    <?php }} else {    ?>                <p style="text-align:center">没有找到产品!</p>            <?php } ?>        </ul>    </div>    <div class="cleared"></div>    <div align="center" class="p-page"><?php            $this->widget('CLinkPager', array(                'pages' => $pages,            ))            ?>    </div></div></div>